    internal class BoxView : Panel
    {
        public PictureBox ImageView { get; private set; }
        public Label ProductName { get; private set; }
        public Label ProductPrice { get; private set; }
        public Panel ButtonsArea { get; private set; }
        public Button AddToCart { get; private set; }
        public Button IncreaseQuantity { get; private set; }
        public Button DecreaseQuantity { get; private set; }
        public Label Quantity { get; private set; }
        public Button RemoveAll { get; private set; }
        public int NumberOfProducts { get; set; }
        public OrderedProduct MainProduct { get; private set; }
        private static Communicator communicator = Communicator.Instance;


        private void InitializeChildren()
