            DecreaseQuantity.Location = new Point(52, 5);
            DecreaseQuantity.Size = new Size(40, 50);
            DecreaseQuantity.FlatStyle = FlatStyle.Flat;

            // Quantity
            Quantity.Location = new Point(92, 5);
            Quantity.Text = "12";
            Quantity.Size = new Size(40, 50);
            Quantity.BackColor = Color.FromArgb(206, 121, 107);
            Quantity.ForeColor = Color.White;
            Quantity.TextAlign = ContentAlignment.MiddleCenter;

            // Increase Quantity
            IncreaseQuantity.FlatAppearance.BorderSize = 0;
            IncreaseQuantity.Text = "+";
            IncreaseQuantity.Location = new Point(132, 5);
            IncreaseQuantity.Size = new Size(40, 50);
            IncreaseQuantity.FlatStyle = FlatStyle.Flat;

            // removeAll
            RemoveAll.BackColor = Color.FromArgb(206, 121, 107);
            RemoveAll.ForeColor = Color.White;
            RemoveAll.FlatAppearance.BorderSize = 0;
            RemoveAll.Text = "Remove All";
            RemoveAll.Size = new Size(120, 50);
            RemoveAll.Location = new Point(52, 60);
